using AutoMapper;using MediatR;using TaskManagement.Domain.Entities;using TaskManagementApplication.Common;using TaskManagementApplication.DTOs.TaskCategory;using TaskManagementApplication.Exception;using TaskManagementApplication.Interfaces;namespace TaskManagementApplication.Features.TaskCategories.Commands.Create{    public class CreateTaskCategoryRequest : IRequest<ResponseModel>    {        public CreateTaskCategoryDTO CreateTaskCategoryDTO { get; set; }    }    public class CreateTaskCategoryCommandHandler : IRequestHandler<CreateTaskCategoryRequest, ResponseModel>    {        private readonly IGenericRepository<TaskCategory> _repo;        private readonly IMapper _mapper;        public CreateTaskCategoryCommandHandler(IGenericRepository<TaskCategory> repo, IMapper mapper)        {            _repo = repo;            _mapper = mapper;        }        public async Task<ResponseModel> Handle(CreateTaskCategoryRequest request, CancellationToken cancellationToken)        {            var response = new ResponseModel();            var validator = new CreateTaskCategoryValidator().Validate(request.CreateTaskCategoryDTO);            if (!validator.IsValid)                throw new ValidationException(validator);            try            {                var entity = _mapper.Map<TaskCategory>(request.CreateTaskCategoryDTO);                var createdEntity = await _repo.CreateAsync(entity);                response.Status = true;                response.Message = "Created Succfully with Id : " + createdEntity.Id;            }            catch            {                response.Status = false;                response.Message = "Failed to create Task Category ! ";            }                       return response;        }    }}